@name           Julia Documentation Dark
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A darker version of the Julia documentation
@author         Coalpha
==/UserStyle== */

body {
    background: #282a36;
    color: #ccc;
  }
  .nav-anchor {
    color: #f8f8f2 !important;
  }
  nav.toc {
    background: #282a36;
    box-shadow: none;
    border-right: 2px solid #567bcd;
  }
  .logo {
    filter: invert(1) hue-rotate(175deg);
  }
  .toctext {
    color: #eee !important;
  }
  .toctext:hover {
    background: #44475a !important;
  }
  .current > .toctext {
    background: #567bcd !important;
    border: none !important;
  }
  .internal {
    box-shadow: none !important;
    background: #235 !important;
  }
  a {
    color: #8be9fd;
  }
  a:hover {
    color: #7dd2e4;
  }
  pre {
    border-radius: 0px;
    border-top: 4px solid #6272a4;
    border-left: 4px solid #6272a4;
    border-right: none;
    border-bottom: none;
    background:#282a36;
  }
  code, code.language-julia {
    color: #f8f8f2;
  }
  code.language-julia-repl {
    color: #6272a4;
  }
  .hljs-meta {
    color: #8be9fd;
  }
  .julia {
    color: #f8f8f2 !important;
  }
  .hljs-number {
    color: #bd93f9;
  }
  .hljs-string {
    color: #f1fa8c;
  }
  .hljs-keyword {
    color: #ff79c6;
  }
  .hljs-built_in {
    color: #ff5555;
  }
  .hljs-literal {
    color: #50fa7b;
  }
  td {
    background: #282a36;
  }
  #topbar {
    background: #1b1c23 !important;
    box-shadow: none !important;
  }
  .admonition-title {
    background: #ffb86c !important;
    color: #1b1c23;
  }
  .admonition-text, .admonition.note {
    background: #44475a !important;
  }
  .hljs-subst {
    color: #888ba3 !important;
  }

