    content: url("https://www.khronos.org/assets/css/images/khronos-logo-inverted.svg");
}
h1,
h2,
h3,
#toctitle,
.sidebarblock > .content > .title,
h4,
h5,
h6 {
    border-bottom: 1px solid #31363b;
    color: #eff0f1;
}
#header > h1:first-child {
    color: #eff0f1;
}
a,
a:visited {
    text-decoration: none;
    color: #3daee6;
}
a:hover {
    color: #3daee6;
}
a:focus {
    color: #1e92ff;
}
.literalblock pre,
.listingblock pre:not(.highlight),
.listingblock pre[class="highlight"],
.listingblock pre[class^="highlight "],
.listingblock pre.CodeRay,
.listingblock pre.prettyprint,
.wrapper {
    background: #232629;
}
pre,
pre > code {
    color: #eff0f1;
}
.subheader,
.admonitionblock td.content > .title,
.audioblock > .title,
.exampleblock > .title,
.imageblock > .title,
.listingblock > .title,
.literalblock > .title,
.stemblock > .title,
.openblock > .title,
.paragraph > .title,
.quoteblock > .title,
table.tableblock > .title,
.verseblock > .title,
.videoblock > .title,
.dlist > .title,
.olist > .title,
.ulist > .title,
.qlist > .title,
.hdlist > .title {
    color: #eff0f1;
}
