@namespace      USO Archive
@author         nic96
@description    `A dark theme for the Khronos site based on the KDE Plasma Breeze Dark theme`
@version        20200125.5.36
@license        CC0-1.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("www.khronos.org") {
body {
    background: #31363b;
    color: #eff0f1;
}
.khr-nav {
    border-bottom: 4px solid #232629;
    background: #31363b;
}
