/* ==UserStyle==
@name           Crunchyroll - Dark Theme
@namespace      USO Archive
@author         Cookie338
@description    `A dark version of Crunchyroll.com`
@version        20160320.22.9
@license        NO-REDISTRIBUTION
@preprocessor   uso
@advanced dropdown ad-skin "Show/Hide BG image/Ad Skin (if any)" {
	Show "Show*" <<<EOT  EOT;
Hide "Hide" <<<EOT #template_scroller {background: rgb(20,20,20)!important}
#template_skin_leaderboard {display: none !important}
.vmx-skin {display: none !important} EOT;

}
@advanced dropdown footer_menu "Show/Hide footer menu" {
	Show "Show*" <<<EOT  EOT;
Hide "Hide" <<<EOT #footer_menu {display: none !important} EOT;

}
@advanced dropdown fixed-header "Fixed Header* (Menu/Search bar)" {
	Normal "Normal*" <<<EOT  EOT;
Fixed "Fixed" <<<EOT #header_beta {
    position:fixed !important;
    width:100% !important;
}

#template_container {
    margin:70px auto -70px !important;
}

#template_skin_leaderboard {
    margin:60px auto -60px !important;
}   



@-moz-document url-prefix("http://www.crunchyroll.com/store"), url-prefix("https://www.crunchyroll.com/store") {

#header_beta {
    margin:-70px auto !important;
}
} EOT;

}

==/UserStyle== */
