/* Code pre */
  .highlight {
      background-color: #2a2f33;
      border: 1px solid rgba(0, 0, 0, 0.12);
      margin: 0;
      padding: 1.6rem;
  }

  /* Color Scheme: Font and Backgrounds */
  html.docs {
      color: #eee;
      background-color: #363c42;
      font-size: 50%;
  }

  html.docs header {
      background-color: #363c42;
      box-shadow: 0 0 2.25rem rgba(0, 0, 0, 0.5);
  }

  html.docs header .logo a {
      background: url(data:image/svg+xml;base64,);
  }

  html.docs .doc-main {
      background-color: #363c42;
  }

  h1, h2, h3, h4 {
      color: #8c9fb3;
  }

  html.docs .doc-container {
      padding: inherit;
  }

  html.docs .doc-container>div>h2 {
      padding: 4.8rem;
      margin: 0;
  }

  html.docs .btn-repl {
      bottom: 6.0rem;
      right: 6.0rem;
  }

  html.docs .doc-container>div>div {
      padding: 0 4.8rem 4.8rem;
      margin: 0;
      box-shadow: inset 0px 12px 4px -10px rgba(0, 0, 0, .75);
  }
  html.docs .doc-container>div>div:hover:after {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      width: 7px;
      height: 100%;
      background-color: rgba(75, 113, 202, 0.84);
      box-shadow: inset 2px 2px 4px -2px rgba(0, 0, 0, .75);
  }


  /* Function Names (_.countBy, etc... )  */

