sections.code:
body {
  background-color: #222;
  background-image: none;
  color: #bbb;
}

#MathJax_Message {
  background-color: #222;
}



/** Headers **/
h1 {
  color: #999;
}

h2 {
  color: #53648d;
}

/** Ad message on top-right **/
#adzmessage {
  background-color: #222;
  border-color: #333;
}

/** Navigation block on left **/

/* Github Logo */
img[src="/img/github.png"] {
  opacity: 0.5;
}

/* Twitter Logo */
img[src="/img/twitter.png"] {
  opacity: 0.1;
}

#nav li span, #nav li a {
  color: #c0c0c0;
}

#nav li span:hover, #nav li a:hover {
  color: #0ab1ff;
}

/** Box **/
.hljs {
  border-color: #333;
  color: #c0c0c0;
}

/** Image **/
img {
  border-color: #282828;
  opacity: 0.7;
}

img.clean {
  opacity: 0.9;
}

/** Warning box **/
warning {
  background-color: #380806; /* #4f0b08; from #ec4137 */
  border-color: #320705; /* #670f0a; */
  color: #a0a0a0;
}

/** Note **/
note {
  background-color: #0b290b; /* #103d10; */
  border-color: #092409; /* #051505; */
  color: #a0a0a0;
}

/** OpenGL function Pop-up Pane **/

#hover {
  background-color: #222;
  background-image: none;
  border-color: #333;
  opacity: 0.95;
  color: #c0c0c0;
}

#hover h2 {
  color: #b12c42; /* from #823 */
}

#hover h3 {
  color: #4058cf; /* from #238 */
}

/** Ads on left & right pane **/
/* #rightad_container, #ad {
  display: none;
}

div[id*="google_ads_iframe"] {
  display: none;
} */

/** Tables e.g. learnopengl.com/In-Practice/Debugging **/

table, td {
  border-top-color: #333;
  border-bottom-color: #333;
}

th {
  border-bottom-color: #333;
}

tr:nth-child(2n) {
  background-color: #282828;
}

/***** Syntax Highlighting *****/

/** Function **/
fun {
  color: #cf4040; /* from #822 */
}

function {
  border-bottom-color: #333;
}

/** Variable **/
var {
  color: #7575d8; /* #227; */
}

/** Definition **/
def {
  color: #009a00; /* #080 */
}

body {
  color: #999;
}

/* Header */

.tab-conversation.active > a {
  color: #666 !important;
}

/* Login on right */
.unread .notification-icon {
  color: #444;
}

.notification-count {
  color: #ccc;
}

/* Separator */
.nav-primary {
  border-bottom-color: #333;
}

/* 'Recommended' */
.thread-likes .label-count {
  background-color: #333;
  color: #999;
}

/** Comments **/

.textarea-wrapper {
  background-color: #222;
  border-color: #333;
}

.textarea {
  color: #aaa;
}

.post-actions {
  background-color: #282828;
  border-top-color: #333;
}

.wysiwyg__item a.attach span {
  filter: invert(80%);
}

/** Name 'Or Sign up with Disqus' **/
.input--text {
  background-color: #222;
  border-color: #333;
}

.input--text:focus {
  border-color: #666;
}

/** Comment item **/

.vote-down {
  border-left-color: #333;
}

/* Avatar */
.avatar .user {
  background-color: #222;
}

/* Loading */
.see-more {
  border-top-color: #333;
}

/** Footer **/
.disqus-footer__wrapper {
  border-top-color: #333;
}

