  border-color: #092409; /* #051505; */
  color: #a0a0a0;
}

/** OpenGL function Pop-up Pane **/

#hover {
  background-color: #222;
  background-image: none;
  border-color: #333;
  opacity: 0.95;
  color: #c0c0c0;
}

#hover h2 {
  color: #b12c42; /* from #823 */
}

#hover h3 {
  color: #4058cf; /* from #238 */
}

/** Ads on left & right pane **/
/* #rightad_container, #ad {
  display: none;
}

div[id*="google_ads_iframe"] {
  display: none;
} */

/** Tables e.g. learnopengl.com/In-Practice/Debugging **/

table, td {
  border-top-color: #333;
  border-bottom-color: #333;
}

th {
  border-bottom-color: #333;
}

tr:nth-child(2n) {
  background-color: #282828;
}

/***** Syntax Highlighting *****/

/** Function **/
fun {
  color: #cf4040; /* from #822 */
}

function {
  border-bottom-color: #333;
}

/** Variable **/
var {
  color: #7575d8; /* #227; */
}

/** Definition **/
def {
  color: #009a00; /* #080 */
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/") {
body {
  color: #999;
}

/* Header */

.tab-conversation.active > a {
  color: #666 !important;
}

/* Login on right */
.unread .notification-icon {
  color: #444;
}

.notification-count {
  color: #ccc;
}

/* Separator */
.nav-primary {
  border-bottom-color: #333;
}

/* 'Recommended' */
.thread-likes .label-count {
  background-color: #333;
  color: #999;
}

/** Comments **/

.textarea-wrapper {
  background-color: #222;
  border-color: #333;
}

.textarea {
  color: #aaa;
}

.post-actions {
  background-color: #282828;
  border-top-color: #333;
}

.wysiwyg__item a.attach span {
  filter: invert(80%);
}

/** Name 'Or Sign up with Disqus' **/
.input--text {
  background-color: #222;
  border-color: #333;
}

.input--text:focus {
  border-color: #666;
}

/** Comment item **/

.vote-down {
  border-left-color: #333;
}

/* Avatar */
.avatar .user {
  background-color: #222;
}

/* Loading */
.see-more {
  border-top-color: #333;
}

/** Footer **/
.disqus-footer__wrapper {
  border-top-color: #333;
}
}

