    .showforumtopic-messages td,
    .showforumtopic-messages td,
    .comments .more-replies {
        background: var(--bg-color-foreground)!important;
        background-color: var(--bg-color-foreground)!important;
    }

    .bb-spoiler,
    .bb-spoiler-highlight,
    .main-tabs,
    a.forum_main-topic-desc-numcomments,
    .main-tabs a,
    .main-tabs a:hover,
    .showforumtopic-fblike,
    .paginator-lite-disabled,
    .welcome-simulcasts .welcome-simulcasts-episode:hover,
    .paginator-lite,
    .bb-quote,
    li.highlight a,
    .paginator-lite-selected,
    .search-results a:hover,
    input.search-query {
        background: var(--main-bg-color)!important;
        background-color: var(--main-bg-color)!important;
    }

    .main-tabs a.selected,
    #tabs,
    .guestbook-sort-bar,
    .showforumtopic-messages td,
    .showforumtopic-header,
    .paginator-lite,
    .queue-sortable .queue-item {
        background: var(--bg-color-foreground)!important;
        background-color: var(--bg-color-foreground)!important;
    }
    * {
        border: none!important;
    }
    .paginator-lite:hover,
    .header-search-autocomplete a:hover,
    .header-search-autocomplete a:focus,
    .header-search-autocomplete a:hover,
    .header_search_autocomplete_item_focused {
        background: var(--bg-color-foreground)!important;
        background-color: var(--bg-color-foreground)!important;
    }
    .showforumtopic-messages td {
        border-right: 1px solid var(--main-bg-color)!important;
        border-left: 1px solid var(--main-bg-color)!important;
    }

    .showforumtopic-message-user,
    .showforumtopic-message-contents {
        border-right: 6px solid var(--main-bg-color)!important;
        border-top: 6px solid var(--main-bg-color)!important;
    }



    .widget-forumnav-current::before,
    .widget-forumnav-ancestor::before {
        border-left-color: var(--main-bg-color)!important;
    }
    .sub-tabs-menu .calendar-link,
    .new-template-body .block-link:hover,
    .new-template-body .pagination a,
    .new-template-body .para-div a,
    .new-template-body .sub-tabs .selectors a.text-link,
    .new-template-body .text-link,
    .new-template-body .wrapper a:hover a,
    .new-template-body a:hover h4,
    .new-template-body li a,
    .new-template-body p a,
    .new-template-body span a {
        color: var(--color-link) !important;
    }

    * {
        scrollbar-color: var(--scroll) var(--main-bg-color)!important;
        scrollbar-width: auto!important;
    }
    *::-webkit-scrollbar {
        background-color: transparent;
    }
    *::-webkit-scrollbar-thumb {
        background: var(--scroll);
    }
    /*                     _____ .        .
                      /\    |\   |   |   |\      /|
                     /__\   | \  |   |   | \    / |
                    /    \  |  \ |   |   |  \  /  |
                   /      \ |   \| __|__ |   \/   |   */
    body {
        opacity: 0;
        transition: opacity 1s!important;
        overflow-x: hidden!important;
        animation-name: fadein!important;
        animation-duration: 2s!important;
        animation-delay: 1s!important;
        animation-fill-mode: forwards!important;
    }
    .poster {
        transform: translateX(-700px);
        animation-name: slideright!important;
        animation-duration: 1s!important;
        animation-delay: 3s!important;
        animation-fill-mode: forwards!important;
    }


    #showview-content-header .ch-left,
    #source_manga_showview h1.ellipsis {
        transform: translateY(-200px);
        animation-name: slidedown!important;
        animation-duration: 1s!important;
        animation-delay: 3s!important;
        animation-fill-mode: forwards!important;
    }


    #source_manga_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(n + 2),
    #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(n + 2) {
        transform: translateX(100vw);
        animation-name: slideleft!important;
        animation-duration: 1s!important;
        animation-delay: 3s!important;
        animation-fill-mode: forwards!important;
    }

    #source_browse #main_content .portrait-grid li {
        transition: transform 0.3s, box-shadow 0.3s;
    }

    #source_browse #main_content .portrait-grid li:hover {
        transform: var(--scale);
    }

    .landscape-grid li,
    .welcome-countdown-items li,
    .header-search-autocomplete a,
    #source_browse #main_content .portrait-grid li {
        transition: transform 0.3s;
    }
    .welcome-countdown-items li:hover,
    .header-search-autocomplete a:hover,
    .welcome-countdown-items li:focus,
    .header-search-autocomplete a:focus {
        transform: var(--scale);
        background-color: var(--main-bg-color)!important;
    }
    @keyframes slideright {
        from {
            transform: translateX(-500px);
        }
        to {
            transform: translateX(0px);
        }
    }


    @keyframes slideleft {
        from {
            transform: translateX(100vw);
        }
        to {
            transform: translateX(0px);
        }
    }


    @keyframes slidedown {
        from {
            transform: translateY(-200px);
        }
        to {
            transform: translateY(0px);
        }
    }
    @keyframes fadein {
        from {
            opacity: 0;
        }
        to {
            opacity: 1;
        }
    }

.queue-sortable .queue-item {
        width: 95%;
        margin: 10px auto!important;
    }
    .source-home .collection-carousel-arrow {
        width: 36px;
    }

    .series-info {
        margin: 0!important;
    }

    .queue-sortable .queue-item .queue-item-content {
        box-sizing: border-box!important;
        width: 100%;
    }

    .queue-sortable .queue-item .anchor-to-episode {
        box-sizing: border-box!important;
        width: 80%;
        flex: 1 1;
    }

    .source-home .dropdown-collection {
        flex: 0 0 36px;
        margin: 0!important;
        background: url("data:image/png;base64,")
    }

sourceCode:
/* ==UserStyle==
@name           Crunchyroll Enhancer
@namespace      USO Archive
@author         LazyGuy12
@description    `This userstyle tries to enhance the overall look of <a href="https://www.crunchyroll.com">Crunchyroll</a>. Now with a dark mode.Source code on <a href="https://github.com/lazy-guy/crunchyroll-enhancer/">GitHub</a>`
@version        20200127.11.52
@license        CC0-1.0
@preprocessor   uso
@advanced dropdown theme "Theme" {
	tdark "Dark*" <<<EOT :root{
        --main-bg-color: rgb(31, 35, 45);
        --header-bg: rgb(35,45, 56);
        --bg-color-foreground: rgb(42, 45, 62);
	    --scroll: rgb(70, 75, 100);
        --color-text: #ffffff;
        --color-text-orange: rgb(255, 139, 77);
        --color-text-heading: rgb(242, 234, 230);
        --color-text-lighter: rgb(181, 181, 181);
        --color-link: #74cafb;
        --shadow: 0px 0px 38px 5px rgba(0,0,0,0.66);
        --shadow-light: 0px 0px 5px 2px rgba(0,0,0,0.66);
        --scale: scale(1.04);
    } EOT;
tlight "Light" <<<EOT :root{
        --header-bg: rgb(255, 255, 255);
        --shadow: 0px 2px 5px rgba(0, 0, 0, 0.6);
        --scroll: #ddd;
        --bg-color-foreground: rgb(250, 250, 250);
        --main-bg-color: rgb(230, 230, 230);
        --scroll: rgb(168, 168, 168);
        --color-text: #000000;
        --color-text-orange: rgb(255, 139, 77);
        --color-text-heading: rgb(70, 70, 70);
        --color-text-lighter: rgb(112, 112, 112);
        --color-link: rgb(3, 112, 252);
        --shadow-light: 0px 0px 5px 2px rgba(0,0,0,0.66);
        --scale: scale(1.04);
    } EOT;
tgdark "greyish Dark" <<<EOT :root{    
        --main-bg-color: rgb(18, 18, 18);
        --header-bg: rgb(27, 27, 27);
        --bg-color-foreground: rgb(30, 30, 30);
	    --scroll: rgb(70, 75, 90);
        --color-text: #ffffff;
        --color-text-orange: rgb(255, 139, 77);
        --color-text-heading: rgb(242, 234, 230);
        --color-text-lighter: rgb(181, 181, 181);
        --color-link: #74cafb;
        --shadow: 0px 0px 38px 5px rgba(0,0,0,0.66);
        --shadow-light: 0px 0px 5px 2px rgba(0,0,0,0.66);
        --scale: scale(1.04);
    } EOT;
tadark "AMOLED Dark" <<<EOT :root{    
        --main-bg-color: rgb(0, 0, 0);
        --header-bg: rgb(10, 10, 10);
        --bg-color-foreground: rgb(15, 15, 15);
	    --scroll: rgb(70, 75, 90);
        --color-text: #ffffff;
        --color-text-orange: rgb(255, 139, 77);
        --color-text-heading: rgb(242, 234, 230);
        --color-text-lighter: rgb(181, 181, 181);
        --color-link: #74cafb;
        --shadow: 0px 0px 38px 5px rgba(0,0,0,0.66);
        --shadow-light: 0px 0px 5px 2px rgba(0,0,0,0.66);
        --scale: scale(1.04);
    } EOT;
ttdark "Twilight Dark" <<<EOT :root{    
        --main-bg-color: rgb(38, 28, 28);
        --header-bg: rgb(56, 32, 32);
        --bg-color-foreground: rgb(61, 32, 32);
	    --scroll: rgb(70, 75, 100);
        --color-text: #ffffff;
        --color-text-orange: rgb(255, 139, 77);
        --color-text-heading: rgb(242, 234, 230);
        --color-text-lighter: rgb(181, 181, 181);
        --color-link: #74cafb;
        --shadow: 0px 0px 38px 5px rgba(0,0,0,0.66);
        --shadow-light: 0px 0px 5px 2px rgba(0,0,0,0.66);
        --scale: scale(1.04);
    } EOT;
ttlight "Twilight Light" <<<EOT :root{
        --header-bg: rgb(255, 150, 110);
        --shadow: 0px 2px 5px rgba(0, 0, 0, 0.6);
        --scroll: #ddd;
        --bg-color-foreground: rgb(255, 150, 110);
        --main-bg-color: rgb(255, 180, 180);
        --scroll: rgb(255, 110, 90);
        --color-text: #000000;
        --color-text-orange: rgb(255, 139, 77);
        --color-text-heading: rgb(50, 50, 50);
        --color-text-lighter: rgb(80, 80, 80);
        --color-link: rgb(3, 112, 252);
        --shadow-light: 0px 0px 5px 2px rgba(0,0,0,0.66);
        --scale: scale(1.04);
    } EOT;

}
@advanced dropdown qs "Queue Style (Now Defunct)" {
	qsg "Grid*" <<<EOT #source_home .landscape-grid li .queue-wrapper{
        height: auto;
    }
    #source_home .landscape-grid li {
        box-shadow: var(--shadow);
        transition: box-shadow 0.3s, transform 0.3s;
    }
    #source_home .landscape-grid li:hover{
        box-shadow: var(--shadow);
        transform: var(--scale);
    }
    #main_content ul#sortable{
        list-style-type: none;
        display: flex !important;
        flex-wrap: wrap !important;
        justify-content: flex-start;
    }
    #main_content ul#sortable li.queue-item{
        width: 200px !important;
        margin: 10px !important;
        padding: 0px;
        height: 116.5px;
    }
    #main_content ul#sortable li.queue-item .small-margin-bottom{
        margin: 0px;
    }
    #main_content ul#sortable li.queue-item .landscape-element{
        padding: 0px;
    }
    #main_content ul#sortable li.queue-item .episode-img,
    #main_content ul#sortable li.queue-item .episode-img img{
        width: 200px !important;
        height: 112.5px !important;
        margin: 0px !important;
        display: block;
        border: none!important;
    }
    #source_home #main_content .landscape-element{
        width: 100% !important;
    }

    #main_content .landscape-element.episode .play-icon-overlay{
        width: 200px !important;
        height: 112.5px !important;
    }

    #main_content ul#sortable li.queue-item .series-title{
        position: absolute !important;
        bottom: 20px !important;
        left: 0px !important;
        width: 200px !important;
        box-sizing: border-box !important;
        padding: 8px !important;
        color: #ffffff  !important;	
    text-shadow: 2px 2px 3px rgba(0,0,0,0.8) !important;
    }

    #main_content ul#sortable li.queue-item .series-data{
        position: absolute !important;
        bottom: 0px !important;
        left: 0px !important;
        width: 200px !important;
        box-sizing: border-box !important;
        padding: 8px !important;
        color: #ffffff  !important;
        text-shadow: 2px 2px 3px rgba(0,0,0,0.8) !important;
    }
    #main_content ul#sortable li.queue-item .handle{
        position: absolute !important;
        top: 10px !important;
        left: 10px !important;
        z-index: 400 !important;
        height: 100px !important;
    }
    #main_content ul#sortable li.queue-item .series-info p.short-desc, #source_home .queue-to-top, #source_home .dropdown-collection{
        display: none !important;
    }#sidebar .videos .landscape-element .series-info {
        box-sizing: border-box !important;
        padding-left: 5px !important;
    }
    .placeholder{
        width: 200px;
        transform: scaleY(0);
    }
    #source_home .queue-controls{
        position: absolute!important;
        width: 100%!important;
        box-sizing: border-box;
        top: 0;
        left: 0;
        padding: 5px!important;
    }

    #source_home .landscape-grid li:hover .queue-controls{
        display: block!important;
    }
    #source_home .queue-controls a{
        color: #fff!important;
        text-shadow: 2px 2px 3px rgba(0,0,0,0.8) !important;
    }
    #source_home .more-options{
        display: block;
        width: 0px;
        height: 0px;
        box-sizing: border-box;
    }
    .content-menu, .undo-queue-remove .show-title{
        width: 100%!important;
        box-sizing: border-box!important;
    } EOT;
qsl "List" <<<EOT #source_home #main_content .queue-sortable a.landscape-element.episode{
                width: calc(100% - 110px)!important;
                box-sizing:border-box!important;
            }
            #source_home .queue-controls, #source_home #main_content .queue-sortable .more-options, #source_home #main_content .queue-sortable .queue-collection{
                width: 100%!important;
                box-sizing: border-box!important;
            }
            #source_home #main_content .queue-sortable .queue-collection .collection-carousel-scrollable{
                width: auto!important;
                overflow-x: scroll!important;
                position: static!important;
            }
            #source_home #main_content .queue-sortable  .collection-carousel, .collection-carousel-contents{
                margin: 0px!important;
                height: auto!important;
                max-width: 625px!important;
            }
            .collection-carousel-scrollable {
                display: flex!important;
                flex-wrap: nowrap!important;
                padding-left: 10px;
            }
            .queue-collection{
                height: auto!important;
            }

#main_content ul#sortable{
    list-style-type: none !important;
} EOT;

}

==/UserStyle== */
@-moz-document domain("www.crunchyroll.com") {
    /*[[theme]]*/
  :root{
    	--max-width: 1440px;
  }
    #header_beta {
        position: sticky!important;
        top: 0px!important;
        left: 0!important;
        z-index: 999999!important;
    }

    #footer,
        #footer_menu {
            width: 100%!important;
            max-width: none!important;
        }

        #showmedia_free_trial_signup {
            display: none!important;
            width: 0px!important;
            height: 0px;
        }
        #template_skin_leaderboard,
        #skin_splashlink {
            width: 100%!important;
            margin: 0px!important;
            padding: 0px!important;
        }
        #template_skin_leaderboard img {
            width: 100%!important;
            height: auto!important;
        }
        .store {
            background: inherit !important;
            background-color: inherit !important;
        }

        .season ul.portrait-grid {
            scroll-behavior: smooth;
        }

        #template_container,
        .cr-expo-banner {
            box-sizing: border-box!important;
            width: 100%!important;
            min-width: auto!important;
            max-width: none!important;
        }


        #template_container.template-container {
            box-sizing: border-box!important;
            width: 100%!important;
            min-width: auto!important;
            max-width: none!important;
        }


        #source_showview {
            width: 100%!important;
        }

        #source_showview h1 {
            width: 100%!important;
        }

        #main_content,
        #welcome_left,
        .genre-selectors,
        .season-selectors {
            width: 100%!important;
        }
        .portrait-bubble .tooltip-left,
        .game-banner-wrapper {
            display: none!important;
        }
        .portrait-bubble div {
            box-shadow: none;
        }
        #slidebox {
            width: auto;
            max-width: 90vw;
        }
        #sidebar,
        #welcome_right {
            width: 100%!important;
            box-sizing: border-box!important;
            margin: 0px!important;
        }


        #welcome_right #sidebar {
            padding: 10px!important;
            margin: 0px 0px!important;
            box-sizing: border-box!important;
        }

        #source_showview #container {
            display: flex!important;
            flex-direction: column!important;
        }
        #showview-content-header .ch-left {
            box-sizing: border-box!important;
            width: calc(100% - 340px)!important;
            margin-left: 355px!important;
            margin-top: 0px!important;
            position: absolute!important;
            float: none!important;
            z-index: 999;
        }
        #showview-content-header .ch-left h1 {
            font-size: 2.4em!important;
        }
        #source_showview #container #main_content {
            order: 30!important;
        }

        #source_showview #container #sidebar {
            order: 20!important;
            padding: 10px 0px!important;
        }

        #source_showview #container #sidebar #sidebar_elements {
            display: grid!important;
            grid-gap: 15px!important;
            grid-template: 'cover cover about about about' 'cover cover about about about' 'cover cover avail avail avail' 'cover cover rating details details'!important;
        }

        #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(1) {
            grid-area: cover!important;
            text-align: center!important;
        }
        #source_showview .poster {
            width: 340px!important;
            height: auto!important;
            min-height: auto!important;
            max-width: 340px!important;
        }
        #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(2) {
            grid-area: avail!important;
            margin-bottom: 0px!important;
        }

        #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(3) {
            grid-area: about!important;
            margin-bottom: 0px!important;
            padding-top: 30px!important;
        }

        #source_showview #container #sidebar #sidebar_elements li p.description .trunc-desc,
        #source_showview #container #sidebar #sidebar_elements li p.description .more-dots,
        #source_showview #container #sidebar #sidebar_elements li p.description span:last-of-type {
            display: none!important;
        }

        #source_showview #container #sidebar #sidebar_elements li p.description .more {
            display: inline!important;
        }

        #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(4) {
            grid-area: rating!important;
            margin-bottom: 0px!important;
        }

        #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(5) {
            grid-area: details!important;
            margin-bottom: 0px!important;
        }


        #source_manga_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(1) {
            grid-area: cover!important;
            text-align: center!important;
        }
        #source_manga_showview .poster {
            width: 280px!important;
            height: auto!important;
            min-height: auto!important;
            max-width: 300px!important;
        }
        #source_manga_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(2) {
            grid-area: about!important;
            margin-bottom: 0px!important;
            padding-top: 40px!important;
        }

        #source_manga_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(3) {
            grid-area: more!important;
            margin-bottom: 0px!important;
        }

        #source_manga_showview #container #sidebar #sidebar_elements {
            display: grid!important;
            grid-template: 'cover cover about about about' 'cover cover about about about' 'cover cover more more more' 'cover cover more more more'!important;
            grid-gap: 15px!important;
        }


        #source_manga_showview #container {
            display: flex!important;
            flex-direction: column!important;
        }
        #source_manga_showview #container h1.ellipsis {
            box-sizing: border-box!important;
            width: calc(100% - 300px)!important;
            margin-left: 310px!important;
            margin-top: 0px!important;
            position: absolute!important;
            font-size: 2em!important;
        }
        #source_manga_showview #container #main_content {
            order: 30!important;
        }

        #source_manga_showview #container #sidebar {
            order: 20!important;
        }


        .show-actions {
            text-align: center!important;
        }

        #tabs {
            box-shadow: none!important;
            -moz-box-shadow: none!important;
            -webkit-box-shadow: none!important;
        }

        .main-tabs {
            padding: 0px!important;
        }

        .portrait-grid {
            display: flex;
            flex-wrap: wrap;
            justify-content: center!important;
        }

        .share-buttons {
            display: none!important;
        }

        table.comments {
            width: 100%!important;
        }
        .comments .guestbook-body {
            width: calc(100% - 70px)!important;
        }

        .welcome-featured-media,
        .banner-carousel .viewport {
            width: 100%!important;
            height: calc(100vw * 0.57)!important;
        }

        #showview_content_volumes {
            max-width: 640px!important;
            margin: auto!important;
        }

        @media only screen and (min-width: 1001px) {
            #source_browse #main_content,
            #welcome_left,
            #source_home #main_content {
                width: calc(100% - 400px)!important;
                box-sizing: border-box!important;
            }
            #source_browse .portrait-container {
                width: 100%!important;
            }

            #source_browse #sidebar,
            #welcome_right,
            #source_home #sidebar,
            #welcome_right #sidebar {
                width: 300px!important;
                box-sizing: border-box!important;
                padding: 0px!important;
            }
            .welcome-featured-media,
            .banner-carousel .viewport {
                width: 100%!important;
                height: calc((100vw - 400px) * 0.57)!important;
            }

            #source_home #main_content .series-info {
                width: calc(100% - 180px)!important;
            }

            #source_home #main_content .landscape-element {
                flex-grow: 1!important;
                flex-shrink: 1!important;
            }
            #source_home #main_content .series-info {
                flex-grow: 1!important;
                flex-shrink: 1!important;
                width: auto!important;
            }
        }
        #showmedia_video,
