/* original */
#breadcrumbs {
    background-color: #161616;
}
#breadcrumbs> ul {
    margin: 0;
    padding: 4px 15px;
    list-style: none
}
#breadcrumbs>ul>li+li:last-of-type a {
    color: #ddd;
}
.header-bar {
    border-bottom: 0px solid #242424;
    background-color: #161616;
    min-height: 33px;
    padding-top: 15px;
    padding-bottom: 15px;
}
.header-bar:before,
.header-bar:after {
    display: table;
    content: "";
    line-height: 0
}
.header-bar:after {
    clear: both
}
.header-bar.pinned {
    position: fixed;
    top: 0;
    width: 1071px;
    background-color: #ccc;
    box-shadow: 0px 2px 3px rgba( 0, 0, 0, 0.5);
    margin-left: -12px;
    padding: 15px;
    z-index: 100
}
.react-select-box-label {
    color: #2D3B45
}
.react-select-box-click-outside-layer {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: 2
}
.react-select-box-clear {
    position: absolute;
    top: 15px;
    right: 0;
    width: 35px;
    height: 20px;
    background-color: #ddd;
    text-indent: -9999em;
    z-index: 3;
    border: none
}
.react-select-box-clear:before {
    content: 'Ã—';
    position: absolute;
    top: 2px;
    left: 10px;
    z-index: 1;
    background-color: #73818C;
    border-radius: 100%;
    font-size: 13px;
    font-size: .8125rem;
    color: #ddd;
    line-height: 1;
    width: 15px;
    height: 15px;
    text-indent: 0;
    text-align: center
}
.react-select-box-clear:hover:before,
.react-select-box-clear:focus:before {
    background-color: var(--ic-brand-primary)
}
.react-select-box-hidden {
    display: none
}
.react-select-box-options {
    margin: 2px 0 0;
    position: absolute;
    padding: 10px 0;
    width: 240px;
    top: 100%;
    left: 0;
    z-index: 4;
    background-color: #ddd;
    border-radius: 4px
}
.ic-badge.ic-badge--neutral {
