@version        20181016.16.8
@license        CC-BY-4.0
@preprocessor   uso
@advanced color color "Color" #0F92FF
@advanced dropdown syntax "Syntax highlighting" {
	syntax3 "Marshmallow*" <<<EOT .pun, .pln {
    color: #292929;
}
.com {
    color: #999;
}
.post-text pre, .wmd-preview pre {
    filter: invert(1) saturate(200%) hue-rotate(330deg);
} EOT;
syntax1 "Default" <<<EOT .pun, .pln {
    color: #787878;
}
.com {
    color: #454545;
}
.post-text pre, .wmd-preview pre {
   filter: brightness(200%) hue-rotate(0deg) saturate(75%);
} EOT;
syntax4 "Neon" <<<EOT pre code {
    filter: invert(1);
}
pre {
    background-color: #e4e4e4;
    filter: invert(1);
}

.kwd, .dec {
    color: #fc3d74;
}
.pun {
    color: #e4e4e4;
}
.com {
    color: #818181;
}
.pln {
   color: #8eff3e;
}
.typ {
    color: #53AAFF;
}
.lit {
    color: #7395b6;
}
.str, .tag {
    color: #6CE0B6;
} EOT;

}

==/UserStyle== */
