/* Site Header */
  .site-header {
      background: #111111 !important;
      border-bottom: black !important;
      box-shadow: none;
  }

  .login, .login-box {
      border: none !important;
  }

  .dropdown-menu {
      background: #111111 !important;
  }

  div.loginbox-selection {
      border: none !important;
  }

  .login-box {
      background: #111111 !important;
  }

  .login-box:hover a.login-join {
      background: #222222 !important;
      color: #248bfb !important;
  }

  .dropdown-menu {
      border: solid 1px #444444 !important
  }

  .dropdown-menu a, .dropdown-menu div {
      border: none !important;
  }

  .dropdown-menu a:hover {
      background: #111111 !important;
      color: #248bfb !important;
  }

  /* Input */
  .header-search, .search-form, .search-form input, input.full-width, textarea {
      border: none !important;
      color: #248bfb !important;
      background: black !important;
  }

  .search-form input, input.full-width {
      border: solid 1px #444444 !important;
  }

  .search-form a:before, .search-form span:before {
      color: #248bfb !important;
  }

  .search-form a:before, .search-form span.cancel-icon:before {
      color: #ff2727 !important;
  }
  .source-search-results h5, .source-search-results li, div.sources h5, div.sources li {
      border: none !important;
      background: black !important;
  }



  /* Body */
  #push-main, .page-container, .center_content.explore, header, .edit-page, #main, #thing_form, .edit-section, .submit-buttons.fixed, .file-upload, div.subcomponent, fieldset.full-width, div.markdown-preview,
  .customizer-explore, .customizer-what-is, .customizer-contribute, .card-footer, #create-cta, #join-cta {
      border: none !important;
      background: black !important;
  }


  /* Spotlight */
  .spotlight-content {
      opacity: 0.6;
      background: none;
      z-index: 10;
  }

  div.spotlight {
      overflow: visible;
  }

  /* Content Boxes */
  .content-box, .sourcelist, .subcomponent-options, .profile-card, .profile-card .user-info, a.user-count, a.view-analytics {
      background: black !important;
      border: solid 1px #444444 !important;
  }

  .dashboard-notification {
      background: #222222 !important;
      border: solid 1px #444444 !important;
  }

  .content-box h1, .dashboard-notification h1 {
      color: #248bfb !important;
  }

  .content-box h2, .dashboard-notification h2 {
      color: #cccccc !important;
  }

  .content-box span, .dashboard-notification span {
      color: #888888 !important;
  }


  /* Things/Cards */
  .item-header {
      z-index: 1;
      background: rgba(0,0,0,0.5);
  }

  .item-info span {
      color: #eeeeee !important;
  }

  .avatar-wrapper {
      background: black !important;
      border: solid 4px #222222;
  }

  .make-card:hover .item-header, .thing-card:hover .item-header {
      background: rgba(0,0,0,0.8);
  }

  .make-card, .thing-card, .text-card, .group-item {
      background: #222222 !important;
      border: solid 2px #248bfb;
  }

  .make-card img.card-img, .thing-card img.card-img {
      position: absolute;
      clip: rect(0px, 228px, 220px, 64px);
      z-index: 0;
  }
  .item-interactions, .interaction {
      background: #222222 !important;
      border: solid 1px #444444;
      border-left: none;
  }


  /* Buttons */
  div.block_nav {
      border:none !important;
      background: black !important;
  }
  a.block_nav_item {
      border: none !important;
      background: #222222 !important;
      color: #888888 !important;
  }

  a.block_nav_item:hover {
      border:none !important;
      background: #222222 !important;
      color: #248bfb !important;
  }

  a.block_nav_item:disabled {
      border:none !important;
      background: #222222 !important;
      color: #248bfb !important;
  }


  a.block_nav_item:hover::before {
      color: #248bfb !important;
  }

  div.item-interactions {
      border: solid 1px #444444 !important;
      background: #222222 !important;
  }

  a.icon.center, a.button {
      background: #222222 !important;
      color: #888888 !important;

  }

  a.icon.center:hover, a.button:hover {
      background: #222222 !important;
      color: #248bfb !important;

  }


  /* Other */
  .social-tv, .dashboard_button, .content-subjects, .edu-grades a, li, ul, div.add_item, span.attachment {
      border: none;
      background: #222222 !important;
      color: #888888 !important;
  }

  .select-menu, .foldable.edu_fold, select[name="grades"], select[name="subjects"], select[name="standards"] {
      filter: invert(90%);
      border: solid 1px #db7404;
  }


  /* Grey text */
  .logo, h3, div.item-name span a {
      color: #888888 !important;
  }


  /* No border */
  .sourcelist div, .profile-card .user-info, a.view-analytics span, div.follow-stats, div.profile-favorites, div.block_nav, div.thing-contents.content_stack.fixed, a.category-card, div.item-card {
      border: none !important;
  }


   /* Dialog */
  .modal-header, .modal-header h3 {
      background: #248bfb !important;
      color: black !important;
  }


  /* Lite Border */
  textArea {
      border: solid 1px #444444 !important;
  }


  /* Misc Dark */
  div.thing-contents.content_stack.fixed, div.thing-contents.content_stack.fixed li, a.category-card, div.item-card, .dialog-content {
      background: #222222 !important;
  }

  /* Misc Black */ 
  fieldset.attachment-assets, div.upload-progress, div.thing-contents.content_stack li, div.gallery-photo img, .refine-results-fields, .foldable {
      background: black !important;
  }


  /* Misc Thingi-Blue Text */
  h2.section-header {
      color: #248bfb !important;
  }


  /* Footer */
  .site-footer {
      background: #111111 !important;
      border-top: none;
  }

