/* 
 * GitHub Dark v2.6.0
 * Copyright 2016 Quan You.
 * GitHub Project (https://cquanu.github.io/github-dark/)
 * Licensed under MIT (https://github.com/cquanu/github-dark/blob/master/LICENSE)
*/


/* HTML Global */

body {
    background-color: #1a2632;
    color: #fff;
}

p {
    color: #607d8b !important;
}

h4 {
    color: #fff;
}


/* Class Global */

.lead {
    color: #929292;
}


/* Main Site and New User */

.site-header {
    background-color: #243447;
}

.site-header-nav .nav-item {
    color: #fff;
}

.jumbotron-home {
    background-image: none;
    background-color: #1a2632;
}

.features-list .list-divider,
.setup-form .tos-info,
.setup-form .setup-organization-next {
    border-color: #304251;
}

.radio-label {
    border-color: #304251;
}

.tag-input-tag {
    background: #243447;
}

.tag-input-tag .remove {
    background: #1a2632;
}

.home-hero-signup .form-control-lg {
    border-color: #304251;
}

.shade-gray {
    background-color: #121b23 !important;
}

.tile-block {
    border-color: #304251;
}

.tile-bordered:not(:last-child) {
    border-color: #304251;
}


/* Main Site Login */

.session-authentication {
    background-color: #1a2632;
}

.session-authentication .header-logo {
    color: #fff;
}

.session-authentication .auth-form-body {
    border-color: #304251;
}

.auth-form-body {
    background-color: #1a2632;
}

.session-authentication .create-account-callout {
    border-color: #304251;
}

.session-authentication .auth-form-header {
    color: #fff;
}


/* Site Wide Global */

.header {
    background-color: #243447;
    border-color: #304251;
}

.header-logo-invertocat {
    color: #fff;
}

.header-search-scope {
    border-color: #304251;
}

.header-nav-link {
    color: #fff;
}


/*.scoped-search .form-control .header-search-scope {
    background-color: #1a2632;
    border-color: #304251;
}*/

.form-control,
.form-select {
    color: #fff;
    background-color: #151f29;
    border-color: #304251;
}

.form-control.focus,
.form-control:focus,
.form-select.focus,
.form-select:focus {
    box-shadow: none;
}

.form-group .form-control:focus {
    background-color: #151f29;
}

.btn {
    color: #fff;
    background-color: #1a2632;
    background-image: none;
    border-color: #304251;
}

.btn:hover,
.btn:active,
.btn.zeroclipboard-is-hover,
.btn.zeroclipboard-is-active {
    text-decoration: none;
    background-color: #151f29;
    background-image: none;
    border-color: #304251;
}

.btn:active,
.btn.selected,
.btn.zeroclipboard-is-active {
    background-color: #1a2632;
    background-image: none;
    border-color: #304251;
}

.btn-primary {
    color: #fff;
    background-color: #57BB5C;
    background-image: none;
    border-color: #57BB5C;
}

.btn-theme-green {
    background-color: #6cc644;
}

.site-footer {
    border-color: #304251;
}

.dropdown-menu {
    background-color: #1a2632;
    border: 1px solid #304251;
    box-shadow: none;
}

.dropdown-divider {
    background-color: #304251;
}

.dropdown-item {
    color: #9e9e9e;
}

.dropdown-item:hover {
    background-color: #243447;
}

button.dropdown-item.dropdown-signout {
    color: #df3e3e;
}


/* Site Related */

.setup-header {
    border-color: #304251;
}


/* Home */

.news .alert {
    border-color: #304251;
}

.news .alert .octicon {
    color: #8899a6;
}

.news .alert .branch-link,
.news .alert .pull-info {
    color: #151f29;
    background: #627790;
}

.ajax-pagination-form .ajax-pagination-d {
    background: #babdcb;
}

.boxed-group>h3,
.boxed-group .heading {
    color: #fff;
    background-color: #243447;
    border-color: #304251;
}

.boxed-group-inner {
    background: #1a2632;
    border: 1px solid #304251;
}

.mini-repo-list-item {
    border-color: #304251;
}

.mini-repo-list-item .repo-icon {
    color: #8899a6;
}

.private .mini-repo-list-item {
    background-color: #243447;
}

.private .mini-repo-list-item .repo-icon {
    color: #fff9c4;
}

.filter-bar {
    background-color: #1a2632;
    border-bottom: 1px solid #304251;
}

.boxed-group .counter {
    color: #fff;
    background-color: #1a2632;
    border: 1px #304251 solid;
}

.subscribe-feed {
    color: #6d6d72;
}

.protip-callout {
    border-color: #304251;
}

.octofication .broadcast-icon-mask {
    background-color: #1a2632;
}

.link-gray-dark {
    color: #4078c0 !important;
}

.more-repos {
    text-align: center;
    background-color: #243447;
    box-shadow: none;
    border-top: 1px solid #304251;
}


/* New Repo */

.outline-box-highlighted {
    background-color: #1a2632;
    border-color: #304251;
}

.outline-box {
    border: solid 1px #304251;
}

.copyable-terminal {
    background-color: #151f29;
}

.reponame-suggestion {
    color: #4caf50;
}


/* Repo Page (Main) */

.repohead.experiment-repo-nav {
    background-color: #151f29;
}

