.profile-timeline.discussion-timeline::before {
    left: 11px;
    background-color: #304251;
}

.profile-timeline.discussion-timeline .profile-timeline-month-heading::after {
    background-color: #304251;
}

.profile-rollup-wrapper+.profile-rollup-wrapper {
    border-color: #304251;
}


/* Profile | Contribution */

#contributions-calendar rect[fill="#eeeeee"] {
    fill: #151f29;
}


/* Profile | Repo Tab */

.profilecols .filter-bar .filter-selected {
    color: #8485ce;
}


/* Notification */

.list-group-item.navigation-focus {
    background-color: #151f29;
}

.list-group-item-link {
    color: #fdfdfd;
}

.boxed-group-list>li {
    border-color: #304251;
}

.flash {
    color: white;
    background-color: #364952;
    border-color: #4c85a0;
}


/* Organisation */

.org-name {
    color: #fff;
}

.orghead {
    background-color: #151f29;
    border-color: #304251;
}

.simple-box {
    background-color: #1a2632;
    border-color: #304251;
}

.org-module-link {
    color: #00aba5;
}

.member-username {
    color: #00aba5;
}

.org-link {
    color: #00aba5;
}

.subnav-bordered {
    border-color: #304251;
}

.org-module-title {
    border-color: #304251;
}

.member-row {
    border-color: #304251;
}

.pagehead-tabs-item.selected {
    color: #fff;
    background-color: #1a2632;
    border-color: #d26911 #304251 transparent;
}


/* Create Organisation */

.setup-header {
    text-shadow: none;
}

.steps {
    border-color: #304251;
}

.steps li {
    background-color: #1a2632;
    border-color: #304251;
}

.steps li.current {
    color: #9e9e9e;
    background-color: #243447;
}

.setup-info-module {
    background-color: #1a2632;
    border-color: #304251;
    box-shadow: none;
}

.setup-info-module h2 {
    border-color: #304251;
}

.plan-choice.open,
.plan-choice.selected {
    background-color: #1a2632;
}

.plan-choice {
    background-color: #1a2632;
    border-color: #304251;
}


/* Personal Settings (Global) */

.menu-heading {
    color: #fff;
    background-color: #243447;
    border-color: #304251;
}

.listgroup {
    border-color: #304251;
}

.listgroup-item+.listgroup-item {
    border-color: #304251;
}

.oauth-border {
    border-color: #304251;
}

.saved-reply-form {
    border-color: #304251;
}

.user-key-badge {
    border-color: #304251;
}

dl.new-email-form {
    border-color: #304251;
}

.Subhead {
    border-color: #304251;
}


/* Settings | Security */

li.session-device.session-current,
li.session-device {
    background-color: #1a2632;
}

.mute,
.text-gray-dark {
    color: #aeaeae !important;
}

.boxed-group-list>li:first-child {
    border-color: #304251;
}

.boxed-group-inner .help {
    border-color: #304251;
}

.auth-form-header {
    background-color: #243447;
    border-color: #304251;
}


/* Settings | Billing */

.billing-section {
    border-color: #304251;
}

.billing-section .section-content {
    color: #9e9e9e;
}

.radio-label {
    color: #666;
}


/* Modal */

.facebox-popup {
    background-color: #1a2632;
    border: 1px solid #304251;
}

.facebox-alert {
    color: #a9a495;
    background-color: #35342c;
    border-color: #6b5c20;
}

.facebox-header {
    border-color: #304251;
}

.facebox-footer {
    margin: 0 -15px -15;
    background: #243447;
}

.facebox-footer {
    background: #243447;
    border-color: #304251;
}


/* Scrollbar */

::-webkit-scrollbar {
    width: 10px;
}

::-webkit-scrollbar-thumb {
    background-color: #243447;
    border-radius: 0;
}

::-webkit-scrollbar-thumb:hover {
    background-color: #C4C4C4;
}

::-webkit-scrollbar-track {
    background-color: #1a2632;
}


/* Search Results */

.repo-list-item {
    border-color: #304251;
}

.codesearch-results .repo-list-name em,
.codesearch-results .repo-list-description em {
    background-color: #243447;
}

.participation-graph rect {
    fill: #243447;
}

.code-list .file-box {
    border-color: #304251;
}

.code-list .code-list-item+.code-list-item {
    border-color: #304251;
}

.code-list .language {
    color: #9e9e9e;
}

.codesearch-aside .menu .octicon {
    color: #8899a6;
}

.codesearch-aside .filter-list {
    border-color: #304251;
}

.codesearch-aside .filter-list li span.bar {
    background: #243447;
}

.filter-item:hover {
    background-color: #243447;
}


/* Helper */

.timeline-commits .hidden-text-expander .ellipsis-expander {
    color: #8899a6;
    background: #243447;
}

.hidden-text-expander a,
.ellipsis-expander {
    color: #8899a6;
    background: #243447;
}


/* Experimental | Enchancement */


/* Blob Minus */

.blob-num-deletion {
    background-color: #ef9a9a;
}

.blob-code-deletion {
    background-color: #1a2632;
    text-decoration: line-through;
    color: #ef9a9a;
}


/* Blob Plus */

.blob-num-addition {
    background-color: #a5d6a7;
}

.blob-code-addition {
    background-color: #1a2632;
}


/* End of Experimental */


/* GitHub Gists */

.header-logo-wordmark {
    color: #fff;
}

.gist-snippet-meta .description {
    color: #6d6d72;
}


/* Rework Buttons | Icons */

.ajax-pagination-form .ajax-pagination-btn {
    color: #9e9e9e;
    background: #243447;
    border-color: #304251;
}

.ajax-pagination-form .ajax-pagination-btn:hover {
    background-color: #151f29;
}

.btn-outline:disabled,
.btn-outline:disabled:hover,
.btn-outline.disabled,
.btn-outline.disabled:hover {
    border-color: #304251;
}

.btn-octicon {
    color: #8899a6;
}

.toolbar-item {
    color: #8899a6;
}

.toolbar-item .menu-target {
    color: #8899a6;
}

.timeline-comment-actions {
    color: #8899a6;
}

.discussion-sidebar-toggle .octicon {
    color: #8899a6;
}


/* Github Pricing */

.shade-gradient {
    background-image: none;
}

.display-heading-1,
.display-heading-2,
.display-heading-3,
.display-heading-4 {
    color: #fff;
}

.bg-white {
    background-color: #1a2632 !important;
}

.pricing-card {
    background-color: #1a2632;
    border-color: #304251;
    box-shadow: none;
}

.pricing-card-vertical .pricing-card-name {
    border-color: #304251;
}

.pricing-card-vertical .pricing-card-cta {
    border-color: #304251;
}


/* GitHub Explore */

.sort-bar {
    border-color: #304251;
}

.pagehead-nav-item {
    color: #00aba5;
}

.pagehead-nav-item:hover {
    color: #8485ce;
}

.pagehead-nav-item.selected {
    color: #8485ce;
    border-color: #d28e5d;
}

.pagehead.explore-head {
    border-bottom-color: #304251;
}

.exploregrid-item-title {
    color: #fff;
}

.exploregrid-item {
    color: #969ab0;
    border-color: #304251;
    box-shadow: none;
}

.thread-subscription-status {
    background-color: #1a2632;
    border: 1px solid #304251;
}

.explore-section {
    border-color: #304251;
}

.explore-section:nth-child(even) {
    background: #243447;
}

.explore-collection h2 {
    color: #fff;
}

.repo-collection>ul {
    background: #151f29;
    border-color: #304251;
}

.showcase-page-header {
    border-color: #304251;
}

.showcase-page-repo-list {
    border-color: #304251;
}

.showcase-page-pattern::after {
    background: none #1a2632;
}

.intgrs-lstng-item {
    border-color: #304251;
}


/* GitHub Blog */

.blog-title {
    color: #fff;
}


/* Octotree Addon */

.octotree_github_sidebar {
    background-color: #1a2632;
    color: #fff;
}

.octotree_github_sidebar .octotree_views .octotree_view .octotree_view_header {
    background-color: #1a2632;
    border-bottom: 1px solid #304251;
    background-image: none;
}

.octotree_views .octotree_view .octotree_view_header {
    text-shadow: none;
}

.octotree_github_sidebar input,
.octotree_github_sidebar textarea {
    background-color: #151f29;
    border: 1px solid #304251;
    padding: 2px;
    color: #fff;
}

.octotree_github_sidebar .octotree_views .octotree_treeview .jstree-default .jstree-wholerow-clicked {
    background-color: #243447;
}

.octotree_github_sidebar .octotree_views .octotree_treeview .jstree-default .jstree-wholerow-hovered {
    background-color: #243447;
}

.octotree_sidebar {
    border-color: #304251;
}

.octotree_github_sidebar a.octotree_toggle > span::after,
.octotree_github_sidebar a.octotree_opts {
    -webkit-filter: invert(1);
    filter: invert(1);
}


/* End */

sourceCode:
/* ==UserStyle==
@name           GitHub Dark 2.0
@namespace      USO Archive
@author         cquanu
@description    `Dark theme for GitHub - Switch off the lights on GitHub and browse GitHub in nighttime mode.For bug reports, open an issue or submit a pull request on <a href="https://github.com/cquanu/github-dark">GitHub</a>.Alternatively, copy-pasta the styles from <code>github-dark.css</code> into a new userstyle. Be sure to specify that it apply to sites beginning with https://github.com/*.`
@version        20161221.4.44
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("render.githubusercontent.com"), domain("raw.githubusercontent.com"), regexp("^https?://((gist|guides|help|raw|status|developer).)?github.com((?!generated_pages/preview).)*$") {
/* 
 * GitHub Dark v2.6.0
 * Copyright 2016 Quan You.
 * GitHub Project (https://cquanu.github.io/github-dark/)
 * Licensed under MIT (https://github.com/cquanu/github-dark/blob/master/LICENSE)
*/


/* HTML Global */

body {
    background-color: #1a2632;
    color: #fff;
}

p {
    color: #607d8b !important;
}

h4 {
    color: #fff;
}


/* Class Global */

.lead {
    color: #929292;
}


/* Main Site and New User */

.site-header {
    background-color: #243447;
}

.site-header-nav .nav-item {
    color: #fff;
}

.jumbotron-home {
    background-image: none;
    background-color: #1a2632;
}

.features-list .list-divider,
.setup-form .tos-info,
.setup-form .setup-organization-next {
    border-color: #304251;
}

.radio-label {
    border-color: #304251;
}

.tag-input-tag {
    background: #243447;
}

.tag-input-tag .remove {
    background: #1a2632;
}

.home-hero-signup .form-control-lg {
    border-color: #304251;
}

.shade-gray {
    background-color: #121b23 !important;
}

.tile-block {
    border-color: #304251;
}

.tile-bordered:not(:last-child) {
    border-color: #304251;
}


/* Main Site Login */

.session-authentication {
    background-color: #1a2632;
}

.session-authentication .header-logo {
    color: #fff;
}

.session-authentication .auth-form-body {
    border-color: #304251;
}

.auth-form-body {
    background-color: #1a2632;
}

.session-authentication .create-account-callout {
    border-color: #304251;
}

.session-authentication .auth-form-header {
    color: #fff;
}


/* Site Wide Global */

.header {
    background-color: #243447;
    border-color: #304251;
}

.header-logo-invertocat {
    color: #fff;
}

.header-search-scope {
    border-color: #304251;
}

.header-nav-link {
    color: #fff;
}


/*.scoped-search .form-control .header-search-scope {
    background-color: #1a2632;
    border-color: #304251;
}*/

.form-control,
.form-select {
    color: #fff;
    background-color: #151f29;
    border-color: #304251;
}

.form-control.focus,
.form-control:focus,
.form-select.focus,
.form-select:focus {
    box-shadow: none;
}

.form-group .form-control:focus {
    background-color: #151f29;
}

.btn {
    color: #fff;
    background-color: #1a2632;
    background-image: none;
    border-color: #304251;
}

.btn:hover,
.btn:active,
.btn.zeroclipboard-is-hover,
.btn.zeroclipboard-is-active {
    text-decoration: none;
    background-color: #151f29;
    background-image: none;
    border-color: #304251;
}

.btn:active,
.btn.selected,
.btn.zeroclipboard-is-active {
    background-color: #1a2632;
    background-image: none;
    border-color: #304251;
}

.btn-primary {
    color: #fff;
    background-color: #57BB5C;
    background-image: none;
    border-color: #57BB5C;
}

.btn-theme-green {
    background-color: #6cc644;
}

.site-footer {
    border-color: #304251;
}

.dropdown-menu {
    background-color: #1a2632;
    border: 1px solid #304251;
    box-shadow: none;
}

.dropdown-divider {
    background-color: #304251;
}

.dropdown-item {
    color: #9e9e9e;
}

.dropdown-item:hover {
    background-color: #243447;
}

button.dropdown-item.dropdown-signout {
    color: #df3e3e;
}


/* Site Related */

.setup-header {
    border-color: #304251;
}


/* Home */

.news .alert {
    border-color: #304251;
}

.news .alert .octicon {
    color: #8899a6;
}

.news .alert .branch-link,
.news .alert .pull-info {
    color: #151f29;
    background: #627790;
}

.ajax-pagination-form .ajax-pagination-d {
    background: #babdcb;
}

.boxed-group>h3,
.boxed-group .heading {
    color: #fff;
    background-color: #243447;
    border-color: #304251;
}

.boxed-group-inner {
    background: #1a2632;
    border: 1px solid #304251;
}

.mini-repo-list-item {
    border-color: #304251;
}

.mini-repo-list-item .repo-icon {
    color: #8899a6;
}

.private .mini-repo-list-item {
    background-color: #243447;
}

.private .mini-repo-list-item .repo-icon {
    color: #fff9c4;
}

.filter-bar {
    background-color: #1a2632;
    border-bottom: 1px solid #304251;
}

.boxed-group .counter {
    color: #fff;
    background-color: #1a2632;
    border: 1px #304251 solid;
}

.subscribe-feed {
    color: #6d6d72;
}

.protip-callout {
    border-color: #304251;
}

.octofication .broadcast-icon-mask {
    background-color: #1a2632;
}

.link-gray-dark {
    color: #4078c0 !important;
}

.more-repos {
    text-align: center;
    background-color: #243447;
    box-shadow: none;
    border-top: 1px solid #304251;
}


/* New Repo */

.outline-box-highlighted {
    background-color: #1a2632;
    border-color: #304251;
}

.outline-box {
    border: solid 1px #304251;
}

.copyable-terminal {
    background-color: #151f29;
}

.reponame-suggestion {
    color: #4caf50;
}


/* Repo Page (Main) */

.repohead.experiment-repo-nav {
    background-color: #151f29;
}

.pagehead {
    border-color: #304251;
}

.repository-meta {
    color: #9e9e9e;
}

.social-count {
    color: #607d8b;
    background-color: #151f29;
    border: 1px solid #304251;
    border-left: 0;
}

.user-mention,
.team-mention {
    color: #00aba5;
}

.reponav-item {
    color: #9e9e9e;
}

.reponav-item:hover,
.reponav-item:focus {
    color: #fff;
}

.counter {
    background-color: #151f29;
    border: 1px #304251 solid;
}

.reponav-item .counter {
    color: #f9f9f9;
}

.reponav-item.selected {
    color: #fff;
    background-color: #1a2632;
    border-color: #d28e5d #304251 transparent;
}

.btn.selected:hover {
    background-color: #151f29;
}

.overall-summary {
    border-color: #304251;
}

.text-emphasized {
    color: #fff;
}

.numbers-summary a,
.numbers-summary .nolink {
    color: #969ab0;
}

.repository-lang-stats-graph {
    border-color: #304251;
}

.repository-lang-stats ol.repository-lang-stats-numbers li .language-color {
    border: 1px #304251 solid;
}

.repository-lang-stats ol.repository-lang-stats-numbers li .lang {
    color: #fff;
}

table.files {
    background: #1a2632;
}

table.files tr.navigation-focus td {
    background: #151f29;
}

table.files td {
    border-color: #304251;
}

table.files td.icon {
    color: #fff;
}

.warning {
    padding: 0.5em;
    margin-bottom: 0.8em;
    font-weight: bold;
    background-color: #d28e5d;
}

.file-wrap {
    border-color: #304251;
}

.commit-author-section {
    color: #ccc;
}

.commit-tease {
    color: #8485ce;
    background-color: #243447;
    border-color: #3e5570;
}

.commits-list-item .commit-author {
    color: #00aba5;
}

.commit-tease-sha {
    color: #4078c0;
}

.commit-tease-contributors {
    background-color: #151f29;
    border-color: #3e5570;
}

.commit .commit-title a {
    color: #4078c0;
}

.branch-infobar {
    background-color: #1a2632;
    border-color: #3e5570;
}

.full-commit {
    background: #1a2632;
    border: 1px solid #304251;
}

.full-commit p.commit-title {
    color: #00aba5;
}

.full-commit .commit-meta {
    background: #243447;
    border-color: #304251;
}

.full-commit .sha-block {
    color: #fff;
}

.full-commit .sha-block>.sha {
    color: #9e9e9e;
}

.full-commit .btn-outline,
.full-commit .btn-outline:disabled {
    border-color: #304251;
}

.commits-list-item.navigation-focus {
    background: #151f29;
}

.file-navigation .get-repo-btn {
    border-color: #304251;
}

.file-navigation .get-repo-btn:first-child {
    border-color: #304251;
}

.signed-commit-badge {
    border-color: #304251;
}

.signed-commit-signer-name .signer {
    display: block;
    font-weight: bold;
    color: #00aba5;
}

.flex-table-item-primary {
    color: #fff;
}

span.d-block {
    color: #fff;
}

.signed-commit-header {
    background-color: #243447;
    border-color: #304251;
}


/* Repo Blame */

.blame-commit-title,
.blame-commit-title .message {
    color: #4078c0;
}

.blame-commit,
.blame-commit+.blame-line {
    border-color: #304251;
}

.blame-blob-num {
    background-color: #243447;
}

.muted-link {
    color: #00aba5 !important;
}


/* README Markdown Live */

.readme .markdown-body,
.readme .plain {
    background-color: #151f29;
    border-color: #304251;
}

.markdown-body .highlight pre,
.markdown-body pre {
    background-color: #1a2632;
    border: 1px #304251 solid;
}

.markdown-body h1 {
    border-color: #304251;
}

.markdown-body img {
    background-color: #151f29;
}

.markdown-body h1 .octicon-link,
.markdown-body h2 .octicon-link,
.markdown-body h3 .octicon-link,
.markdown-body h4 .octicon-link,
.markdown-body h5 .octicon-link,
.markdown-body h6 .octicon-link {
    color: #fff;
}

.markdown-body table tr {
    background-color: #1a2632;
}

.markdown-body table th,
.markdown-body table td {
    border-color: #304251;
}

.markdown-body table tr:nth-child(2n) {
    background-color: #243447;
}

.markdown-body pre code,
.markdown-body pre tt {
    color: #969ab0;
}

.markdown-body code,
.markdown-body tt {
    color: #151f29;
    background-color: #627790;
}

.blob-num {
    color: #5e6b77;
    border-color: #304251;
}

.blob-code-inner {
    color: #6d6d72;
}

.blob-code-hunk,
.blob-code-expandable {
    background-color: #243447;
}

.blob-num-hunk,
.blob-num-expandable {
    background-color: #243447;
}

.blob-num-expandable {
    background-color: #243447;
}

.ace_editor .ace_gutter-active-line {
    background-color: #243447;
}

.ace_editor .ace_marker-layer .ace_active-line {
    background-color: #243447;
}


/* Code syntax Highlighting */

.pl-pds,
.pl-s,
.pl-s .pl-pse .pl-s1,
.pl-sr,
.pl-sr .pl-cce,
.pl-sr .pl-sra,
.pl-sr .pl-sre {
    color: #4078c0;
}

.pl-s .pl-s1,
.pl-smi {
    color: #bbb;
}

.pl-k {
    color: #b73999;
}

.ace_string {
    color: #4670d8;
}

.ace_support.ace_type {
    color: #b73999;
}

.ace_keyword {
    color: #8485ce;
}

.ace_variable {
    color: #d28e5d;
}

.ace_focus {
