    }

    .boxed-group-list>li:first-child {
        border-color: var(--border-color);
    }

    .boxed-group-inner .help {
        border-color: var(--border-color);
    }

    .auth-form-header {
        background-color: var(--accent-color);
        border-color: var(--border-color);
    }

    .Box-row--gray {
        background-color: var(--accent-color);
    }

    /* Settings | Billing */

    .billing-section {
        border-color: var(--border-color);
    }

    .billing-section .section-content {
        color: #9e9e9e;
    }

    .radio-label {
        color: #666;
    }

    .boxed-group-table th {
        background-color: var(--accent-color);
    }

    .payment-history .refunded, .payment-history .failed {
        background-color: var(--accent-color);
    }

    /* Settings | Developer Settings | Personal Access Tokens */
    .token-scope {
        color: var(--primary-text-color)
    }

    /* Modal */

    .facebox-popup {
        background-color: var(--body-color);
        border: 1px solid var(--border-color);
    }

    .facebox-alert {
        color: #a9a495;
        background-color: #35342c;
        border-color: #6b5c20;
    }

    .facebox-header {
        border-color: var(--border-color);
    }

    .facebox-footer {
        margin: 0 -15px -15;
        background: var(--accent-color);
    }

    .facebox-footer {
        background: var(--accent-color);
        border-color: var(--border-color);
    }

    /* Scrollbar */

    ::-webkit-scrollbar {
        width: 10px;
    }

    ::-webkit-scrollbar-thumb {
        background-color: var(--accent-color);
        border-radius: 0;
    }

    ::-webkit-scrollbar-thumb:hover {
        background-color: #C4C4C4;
    }

    ::-webkit-scrollbar-track {
        background-color: var(--body-color);
    }

    /* Search Results */

    .repo-list-item {
        border-color: var(--border-color);
    }

    .codesearch-results .repo-list-name em,
    .codesearch-results .repo-list-description em {
        background-color: var(--accent-color);
    }

    .participation-graph rect {
        fill: var(--accent-color);
    }

    .code-list .file-box {
        border-color: var(--border-color);
    }

    .code-list .code-list-item+.code-list-item {
        border-color: var(--border-color);
    }

    .code-list .language {
        color: #9e9e9e;
    }

    .codesearch-aside .menu .octicon {
        color: var(--icon-color);
    }

    .codesearch-aside .filter-list {
        border-color: var(--border-color);
    }

    .codesearch-aside .filter-list li span.bar {
        background: var(--accent-color);
    }

    .filter-item:hover {
        background-color: var(--accent-color);
    }

    /* Helper */

    .timeline-commits .hidden-text-expander .ellipsis-expander {
        color: var(--icon-color);
        background: var(--accent-color);
    }

    .hidden-text-expander a,
    .ellipsis-expander {
        color: var(--icon-color);
        background: var(--accent-color);
    }

    /* Experimental | Enchancement */

    /* Blob Minus */

    .blob-num-deletion {
        background-color: #3b2343;
    }

    .blob-code-deletion {
        background-color: var(--body-color);
        text-decoration: line-through;
        color: #ef9a9a;
    }

    /* Blob Plus */

    .blob-num-addition {
        background-color: #a5d6a7;
    }

    .blob-code-addition {
        background-color: var(--body-color);
    }

    .blob-expanded .blob-num,
    .blob-expanded .blob-code {
        background-color: var(--accent-color);
    }

    /* End of Experimental */

    /* GitHub Gists */

    .header-logo-wordmark {
        color: var(--primary-text-color);
    }

    .gist-snippet-meta .description {
        color: var(--code-color);
    }

    /* Rework Buttons | Icons */

    .ajax-pagination-form .ajax-pagination-btn {
        color: #9e9e9e;
        background: var(--accent-color);
        border-color: var(--border-color);
    }

    .ajax-pagination-form .ajax-pagination-btn:hover {
        background-color: var(--depth-color);
    }

    .btn-outline {
        background-color: var(--body-color);
    }

    .btn-outline:disabled,
    .btn-outline:disabled:hover,
    .btn-outline.disabled,
    .btn-outline.disabled:hover {
        border-color: var(--border-color);
    }

    .btn-octicon {
        color: var(--icon-color);
    }

    .toolbar-item {
        color: var(--icon-color);
    }

    .toolbar-item .menu-target {
        color: var(--icon-color);
    }

    .timeline-comment-actions {
        color: var(--icon-color);
    }

    .discussion-sidebar-toggle .octicon {
        color: var(--icon-color);
    }

    h2 span {
        color: var(--secondary-text-color);
    }

    div.search-form.big button, div.search-form.big input {
        background-color: var(--accent-color);
    }

    /* Github Pricing */

    .shade-gradient {
        background-image: none;
    }

    .display-heading-1,
    .display-heading-2,
    .display-heading-3,
    .display-heading-4 {
        color: var(--primary-text-color);
    }

    .bg-white {
        background-color: var(--body-color) !important;
    }

    .pricing-card {
        background-color: var(--body-color);
        border-color: var(--border-color);
        box-shadow: none;
    }

    .pricing-card-vertical .pricing-card-name {
        border-color: var(--border-color);
    }

    .pricing-card-vertical .pricing-card-cta {
        border-color: var(--border-color);
    }

    /* GitHub Explore */

    .sort-bar {
        border-color: var(--border-color);
    }

    .pagehead-nav-item {
        color: #00aba5;
    }

    .pagehead-nav-item:hover {
        color: #8485ce;
    }

    .pagehead-nav-item.selected {
        color: #8485ce;
        border-color: #d28e5d;
    }

    .pagehead.explore-head {
        border-bottom-color: var(--border-color);
    }

    .exploregrid-item-title {
        color: var(--primary-text-color);
    }

    .exploregrid-item {
        color: #969ab0;
        border-color: var(--border-color);
        box-shadow: none;
    }

    .thread-subscription-status {
        background-color: var(--body-color);
        border: 1px solid var(--border-color);
    }

    .explore-section {
        border-color: var(--border-color);
    }

    .explore-section:nth-child(even) {
        background: var(--accent-color);
    }

    .explore-collection h2 {
        color: var(--primary-text-color);
    }

    .repo-collection>ul {
        background: var(--depth-color);
        border-color: var(--border-color);
    }

    .showcase-page-header {
        border-color: var(--border-color);
    }

    .showcase-page-repo-list {
        border-color: var(--border-color);
    }

    .showcase-page-pattern::after {
        background: none var(--body-color);
    }

    .intgrs-lstng-item {
        border-color: var(--border-color);
    }

    div.application-main > div.border-bottom,
    div.application-main div.container-xl > div.position-relative div[style] {
        background: var(--body-color) !important;
    }

    /* GitHub Blog */

    .blog-title {
        color: var(--primary-text-color);
    }

    /* Octotree Addon */

    .octotree_github_sidebar .octotree_views {
        background-color: var(--body-color);
        border-color: var(--border-color);
    }

    .octotree_github_sidebar .octotree_views .octotree_view .octotree_view_header {
        background-color: var(--body-color);
        border-color: var(--border-color);
        border-right-color: var(--border-color) !important;
        border-bottom-color: var(--border-color) !important;
    }

    .octotree_sidebar .ui-resizable-e {
        background-color: var(--border-color);
    }

    .markdown-body .task-list-item.hovered {
        background: #424a7e !important;
    }

    .discussion-item .renamed-was,
    .discussion-item .renamed-is {
        color: #607d8b;
    }

    .octotree-sidebar.octotree-github-sidebar .octotree-views {
        background-color: var(--body-color);
    }

    .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-tree-view .jstree-default .jstree-wholerow-clicked,
    .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-tree-view .jstree-default .jstree-wholerow-hovered {
        color: var(--primary-text-color);
        background-color: var(--accent-color) !important;
    }

    .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-tree-view .jstree-anchor,
    .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-view .jstree-anchor {
        color: var(--primary-text-color);
    }

    /** arrows for folders **/
    .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-tree-view .jstree-default .jstree-no-dots .jstree-open > .jstree-ocl::before,
    .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-tree-view .jstree-default .jstree-no-dots .jstree-closed > .jstree-ocl::before {
        color: var(--icon-color) !important;
    }

    .octotree-sidebar.octotree-github-sidebar .octotree-footer {
        background-color: var(--accent-color);
    }

    .octotree-sidebar.octotree-github-sidebar .octotree-footer .octotree-ad-small a {
        color: var(--secondary-text-color) !important;
    }
    /* End */

    /* Fix new additions to github. */

    .topic-tag {
        background-color: var(--border-color);
    }

    .topic-tag-action {
        background-color: var(--accent-color);
    }

    .delete-topic-button {
        background-color: var(--accent-color);
        border-left-color: var(--border-color);
    }

    .simple-box-footer {
        background-color: var(--border-color);
        border-top: var(--border-color);
    }

    .pagehead-tabs-item:hover {
        color: var(--primary-text-color);
        text-decoration: none;
    }

    .pagehead-tabs-item .counter {
        color: var(--primary-text-color);
        opacity: .85;
    }

    .reaction-summary-item {
        background-color: var(--body-color) !important;
    }

    /* calender */

    .calendar-graph rect[fill="#ebedf0"],
    .calendar-graph rect[fill="#c6e48b"],
    .calendar-graph rect[fill="#7bc96f"],
    .calendar-graph rect[fill="#239a3b"],
    .calendar-graph rect[fill="#196127"],
    .heat {
        background-color: #4183C4 !important;
        fill: #4183C4 !important;
        opacity: .15;
    }

    .contrib-legend {
        display: none !important;
    }

    .calendar-graph .days-selected rect.day.active {
        stroke: #ddd !important;
    }

    .calendar-graph rect.day:hover {
        stroke: #fff !important;
    }

    /* reg colors */

    .calendar-graph rect[fill="#c6e48b"] {
        opacity: .3;
    }

    .calendar-graph rect[fill="#7bc96f"] {
        opacity: .5;
    }

    .calendar-graph rect[fill="#239a3b"] {
        opacity: .7;
    }

    .calendar-graph rect[fill="#196127"] {
        opacity: 1;
    }

    /* user fix */

    .underline-nav-item {
        color: #6a737d;
    }

    .underline-nav-item .counter {
        color: var(--primary-text-color);
        opacity: .65;
    }

    .underline-nav-item:hover {
        color: var(--primary-text-color);
    }

    .notification-indicator .mail-status {
        background-color: #00aba5 !important;
        background-image: none !important;
    }

    .vcard-username {
        color: var(--primary-text-color);
        opacity: .65;
    }

    /* User Circled Images */

    .vcard-avatar img {
        border-radius: 100% !important;
        border: 3px solid var(--border-color) !important;
    }

    /* settings user fix */

    .Box .Box-row .listgroup-item-body {
        color: var(--secondary-text-color) !important;
    }

    .Box .Box-row .listgroup-item-title {
        opacity: .85;
    }

    /* feed fix, user & owner color */

    .simple .title a:first-child,
    .body .title a:first-child,
    .public .author a,
    .owner {
        color: #00aba5 !important;
    }

    .member-name {
        color: #FFF;
        opacity: .75;
    }

    .member-name:hover {
        opacity: 1;
    }

    /* org fix */

    .UnderlineNav {
        border-color: var(--border-color);
    }

    .unstyled-members-count,
    .js-repositories-count {
        color: #FFF;
    }

    .js-stat-label {}

    .note-emphasis {
        color: #FFF !important;
    }

    .team-actions,
    .team-info-card,
    .stats-group-stat {
        border-color: var(--border-color) !important;
    }

    /* blog */

    .blog-post-title a {
        color: #FFF !important;
        opacity: .85;
    }

    .blog-post-body h2 {
        border-color: var(--border-color) !important;
    }

    .blog-post-body h2,
    .blog-post-body h3 {
        color: #FFF;
    }

    .blog-post-body ul,
    .blog-post-body ol {
        color: var(--secondary-text-color);
    }

    .pagination,
    .pagination a,
    .pagination span {
        border-color: var(--border-color) !important;
        background-color: var(--body-color) !important;
    }

    .pagination a,
    .pagination .next_page,
    .pagination .previous_page {
        color: #FFF;
    }

    .meta-item:nth-child(2) a {
        color: #00aba5 !important;
    }

    .site-footer-marketing-col ul li a {
        color: #0366d6 !important;
    }

    /* fix #39 */

    .code-list .divider .blob-num,
    .code-list .divider .blob-code {
        background-color: var(--depth-color);
    }

    /* codemirror vs ace fix */

    .CodeMirror,
    .CodeMirror-lines,
    .CodeMirror-gutters {
        background-color: var(--body-color) !important;
    }

    /* editor: line nums */

    .CodeMirror-linenumber {
        color: #5e6b77 !important;
        border-color: var(--border-color);
    }

    /* editor: syntax */

    .CodeMirror-line span {
        color: var(--code-color);
    }

    .cm-string {
        color: #4078c0 !important;
    }

    .cm-keyword {
        color: #b73999 !important;
    }

    .cm-property,
    .cm-atom,
    .cm-number,
    .cm-meta {
        color: #0086b3 !important;
    }

    .cm-qualifier,
    .cm-variable-3 {
        color: #795da3 !important;
    }

    .cm-tag {
        color: #63a35c !important;
    }

    /** Marketplace **/

    .MarketplaceSideNav {
        background-color: var(--body-color);
        border-right: 1px solid var(--accent-color);
    }

    .MarketplaceBackground-buffer {
        background-color: var(--body-color);
    }

    div.application-main > div.border-bottom,
    div.application-main div.container-xl > div.position-relative div[style] {
        background: var(--body-color) !important;
    }

    /** Repo Security Tab **/

    .SideNav-item {
        background-color: var(--body-color);
        color: var(--secondary-text-color);
        border-top: 1px solid var(--border-color);
    }

    .SideNav-item:hover {
        background-color: var(--depth-color);
    }

    .SideNav-item.selected {
        background-color: var(--depth-color) !important;
        color: var(--primary-text-color);
    }

    /** Repo Graph | Network **/

    canvas{
        background-color: var(--color-bg-canvas);
        color: var(--color-text-primary);
    }

.main-nav {
    min-width: 0;
    width: 8vw;
    transition: ease-in-out 150ms
}

.main-nav:hover {
    width: 340px;
}

.start-chat .fab {
    min-width: 0;
    width: 4vw;
    transition: ease-in-out 150ms;
    text-overflow: clip
}

.start-chat span .fab-label {
    min-width: 0;
    width: 4vw;
    transition: ease-in-out 150ms;
    text-overflow: clip;
}

.snippet-text {
    display: none;
}

.sidebar {
    min-width: 0;
    width: 10vw;
}

.ng-star-inserted .router-wrapper {
    width: 90vw;
}

.info-box[_ngcontent-c5] .title[_ngcontent-c5] {
    color: white;
}

sourceCode:
/* ==UserStyle==
@name Google Dark Search
@version 0.7.29
@description Recolors Google Search, highly customizable.
@author hyperfekt
@namespace net.hyperfekt
@homepageURL https://github.com/hyperfekt/google-dark/
@supportURL https://github.com/hyperfekt/google-dark/issues/
@updateURL https://raw.githubusercontent.com/hyperfekt/google-dark/master/search/search.user.css
@copyright hyperfekt
@license Network Extended License (https://github.com/hyperfekt/google-dark/blob/master/LICENSE.md)
@preprocessor stylus


@var text main-bg "Main Background Color" #20
@var text main-bg-em "Main Background (Lighter)" #32
@var text main-bg-off "Main Background (Darker)" #16
@var text main-bg-container "Main Background (Container)" #20
@var text main-background "Background" main-bg
@var text main-text "Main Text" #DD
@var text main-text-em "Main Text (Lighter)" #EA
@var text main-text-off "Main Text (Darker)" #A9
@var text main-text-minor "Main Text (Darkest)" #74
@var text main-text-disabled "Main Text (Disabled)" #5F
@var text main-link "Main Link" #5A80FF
@var text main-link-visited "Main Link (Visited)" #9268D4
@var text main-source "Main Source" #4DAB6F
@var text contrast "Contrast Multiplier" 1
@var text main-border "Main Border" alpha(black, 0.9 * contrast)
@var text main-separator "Main Separator" alpha(white, 0.15 * contrast)
@var text header-bg "Header Background" #33
@var text header-text "Header Text" #FF
@var text header-text-off "Header Text (Darker)" #C8
@var text footer-bg "Footer Background"  #33
@var text footer-separator "Footer Separator" alpha(white, 0.2 * contrast)
@var text footer-text "Footer Text" #FF
@var text footer-text-off "Footer Text (Darker)" #B4
@var text footer-text-minor "Footer Text (Darkest)" #96
@var text dropdown-checkmark "Dropdown Checkmark Image" url("data:image/png;base64,")
@var text unfilled-star "Rating Star Background" url("data:image/png;base64,")
@var text unfilled-stars "Rating Stars Background" url("data:image/png;base64,")
@var text container-separator "Container Separator" alpha(black, 0.3 * contrast)
@var text popout-border "Popout Border" darken(main-bg-off, 15%)
@var checkbox result-containers "Result Containers" 0
@var text accent-color "Accent Color" #4285f4

==/UserStyle== */

/* Uncomment for manual compilation
	// Main Background
	main-bg = #20;
	// Main Background (Lighter)
	main-bg-em = #32;
	// Main Background (Darker)
	main-bg-off = #16;
	// Main Background (Container)
	main-bg-container = #20;
	// Background
	main-background = main-bg;
	// Main Text
	main-text = #DD;
	// Main Text (Lighter)
	main-text-em = #E6;
	// Main Text (Darker)
	main-text-off = #A9;
	// Main Text (Darkest)
	main-text-minor = #74;
	// Main Text (Disabled)
	main-text-disabled = #5F;
	// Main Link
	main-link = #5A80FF;
	// Main Link (Visited)
	main-link-visited = #9268D4;
	// Main Source
	main-source = #4DAB6F;
	// Contrast Multiplier
	contrast = 1;
	// Main Border
	main-border = alpha(black, 0.9 * contrast);
	// Main Separator
	main-separator = alpha(white, 0.15 * contrast);
	// Header Background
	header-bg = #33;
	// Header Text
	header-text = #FF;
	// Header Text (Darker)
	header-text-off = #C8;
	// Footer Background
	footer-bg =  #33;
	// Footer Separator
	footer-separator = alpha(white, 0.2 * contrast);
	// Footer Text
	footer-text = #FF;
	// Footer Text (Darker)
	footer-text-off = #B4;
	// Footer Text (Darkest)
	footer-text-minor = #96;
	// Dropdown Checkmark Image
	dropdown-checkmark = url("data:image/png;base64,");
	// Rating Star Background
	unfilled-star = url("data:image/png;base64,");
	// Rating Background
	unfilled-stars = url("data:image/png;base64,");
	// Container Separator
	container-separator = alpha(black, 0.3 * contrast);
	// Result Containers
	result-containers = 0;
	// Accent Color
	accent-color = #4285f4;
*/

/* mixins */
	global-definitions() {
		/*
	* Google/Gmail's 3D Loader in Pure CSS
	* https://github.com/rgthree/google-loader-css
	* Regis Gaughan, III <http://regisgaughan.com>
	* MIT License
	*/
		@keyframes gloader-base {
			0%   {transform:rotate(0deg);   background-color:#21aa29;}
			25%  {transform:rotate(90deg);  background-color:#2159d6;}
			50%  {transform:rotate(180deg); background-color:#d62408;}
			75%  {transform:rotate(270deg); background-color:#ffcf00;}
			100% {transform:rotate(360deg); background-color:#21aa29;}
		}

		@keyframes gloader-reveal {
			0%   {background-color:#2159d6; transform:rotate(0deg);}
			25%  {background-color:#d62408; transform:rotate(180deg);}
			50%  {background-color:#ffcf00; transform:rotate(0deg);}
			75%  {background-color:#21aa29; transform:rotate(180deg);}
			100% {background-color:#2159d6; transform:rotate(0deg);}
		}

		/* Green >> DarkGreen | DarkBlue >> Blue >> DarkBlue | DarkRed >> Red >> DarkRed | DarkYellow >> Yellow >> DarkYellow | DarkGreen >> Green */
		@keyframes gloader-flip {
			0.01%        {background-color:#21aa29; transform:rotateY(0deg);}
			12.5%,
			87.56%    {background-color:#105514;}
			12.51%,
			37.5%     {background-color:#102c6b;}
			24.99%       {background-color:#2159d6;}
			37.51%,
			62.5%     {background-color:#6b1204;}
			49.99%       {background-color:#d62408;}
			62.51%,
			87.5%     {background-color:#7f6700;}
			74.99%, 78%  {background-color:#ffcf00;}
			99.99%      {background-color:#21aa29; transform:rotateY(-720deg);}
		}
	}

	popout($important = null) {
		background-color: main-bg $important;
		border-color: popout-border $important;
		box-shadow: 0 2px 10px alpha(black, 0.3 * contrast) $important;
		color: main-text $important;
	}
	dropdown-hover($important = null) {
		background-color: main-bg-off $important;
	}
	button($important = null) {
		background-image: linear-gradient(to bottom, alpha(black, 0.05 * contrast), alpha(black, 0.1 * contrast)) $important;
		border-color: main-separator $important;
		color: main-text-off $important;
	}
