				if $light-accent {
					color: black;
				}
			}
			/* hover */
				.jfk-button-action:hover {
					background-color: accent-shifted(0deg, -9%, -5%);
					background-image: linear-gradient(to bottom, accent-shifted(0deg, 10%, 4%), accent-shifted(0deg, -9%, -5%));
					border-color: accent-shifted(4deg, -30%, -16%);
				}
		/* cancel button */
			.jfk-button-standard, .jfk-button-standard.jfk-button-clear-outline {
				button();
				background-color: main-bg-em;
			}
			.jfk-button-standard.jfk-button-hover, .jfk-button-standard.jfk-button-clear-outline.jfk-button-hover {
				button-hover();
				background-color: main-bg-em;
			}
		/* sign in hint */
			#Gm4Vke span {
				color: main-text-off !important;
			}
}

