        }
        to {
            transform: translateX(0px);
        }
    }


    @keyframes slideleft {
        from {
            transform: translateX(100vw);
        }
        to {
            transform: translateX(0px);
        }
    }


    @keyframes slidedown {
        from {
            transform: translateY(-200px);
        }
        to {
            transform: translateY(0px);
        }
    }
    @keyframes fadein {
        from {
            opacity: 0;
        }
        to {
            opacity: 1;
        }
    }
}

