        scrollbar-color: var(--scroll) var(--main-bg-color)!important;
        scrollbar-width: auto!important;
    }
    *::-webkit-scrollbar {
        background-color: transparent;
    }
    *::-webkit-scrollbar-thumb {
        background: var(--scroll);
    }

    .calendar::after{
        background: linear-gradient(to left, var(--main-bg-color) ,rgba(255,255,255,0))!important;
    }

    .calendar::before{
        background: linear-gradient(to right, var(--main-bg-color) ,rgba(255,255,255,0))!important;
    }

    .library-forum-table{
        border: 3px solid var(--bg-color-foreground)!important;
    }

    svg#cr_logo_noTagline path{
        fill: #2222ff!important;
    }

    /*                             _____ .        .
                      /\    |\   |   |   |\      /|
                     /__\   | \  |   |   | \    / |
                    /    \  |  \ |   |   |  \  /  |
                   /      \ |   \| __|__ |   \/   |   */
    body {
        opacity:1;
    }


    #source_browse #main_content .portrait-grid li {
        transition: transform 0.3s, box-shadow 0.3s;
    }

    #source_browse #main_content .portrait-grid li:hover {
        transform: var(--scale);
    }

    .landscape-grid li,
    .welcome-countdown-items li,
    .header-search-autocomplete a,
    #source_browse #main_content .portrait-grid li {
        transition: transform 0.3s;
    }
    .welcome-countdown-items li:hover,
    .header-search-autocomplete a:hover,
    .welcome-countdown-items li:focus,
    .header-search-autocomplete a:focus {
        transform: var(--scale);
        background-color: var(--main-bg-color)!important;
    }
    @keyframes slideright {
        from {
            transform: translateX(-500px);
        }
        to {
            transform: translateX(0px);
        }
    }


    @keyframes slideleft {
        from {
            transform: translateX(100vw);
        }
        to {
            transform: translateX(0px);
        }
    }


    @keyframes slidedown {
        from {
            transform: translateY(-200px);
        }
        to {
            transform: translateY(0px);
        }
    }
    @keyframes fadein {
        from {
            opacity: 0;
        }
        to {
            opacity: 1;
        }
    }

@media only screen and (min-width: 1px) {
        .series-info {
            margin: 0!important;
        }

        .queue-sortable .queue-item .queue-item-content {
            box-sizing: border-box!important;
            width: 100%;
        }

        .queue-sortable .queue-item .anchor-to-episode {
