/* ==UserStyle==
@name           Dark LeetCode
@namespace      USO Archive
@author         abhishekchakraborty
@description    `Dark theme for coding challenges site, <a href="https://leetcode.com">LeetCode</a>`
@version        20200419.9.46
@license        CC-BY-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("leetcode.com") {
.content-wrapper, html, body {
  color: #aaa;
  background-color: #282828;
}

.btn-default {
  color: #aaa;
  background-color: #222;
}

.btn-default:hover {
  color: #aaa;
  background-color: #333;
}

.btn.focus, .btn:focus, .btn:hover {
  color: #ccc;
